import React, { useReducer, createContext } from 'react';
import axios from 'axios';
import { baseURL, users } from '../../config/config';
import AuthContext from './contextAuth';
import AuthReducer from './authReducer';

import {
  REGISTER_SUCCESS,
  REGISTER_FAIL,
  USER_LOADED,
  AUTH_ERROR,
  LOGIN_SUCCESS,
  LOGIN_FAIL,
  LOGOUT,
} from '../types';

const AuthState = (props) => {
  const initialState = {
    token: localStorage.getItem('token'),
    user: null,
    isAuthenticated: null,
    loading: true,
    error: null,
  };

  const [state, dispatch] = useReducer(AuthReducer, initialState);

  //load user

  //register user
  const register = async (userData) => {
    const config = {
      headers: {
        'Content-Type': 'application/json',
      },
    };

    try {
      const res = await axios.post(baseURL + users, userData, config);
      dispatch({ type: REGISTER_SUCCESS, payload: res.data });
    } catch (error) {
      let errorObj = null;
      if (error.response.data.errors) {
        errorObj = error.response.data.errors;
      } else {
        errorObj = [{ msg: error.response.data.message }];
      }
      dispatch({ type: REGISTER_FAIL, payload: errorObj });
      debugger;
    }
  };

  //login user

  //logout

  //clear errors

  return (
    <AuthContext.Provider
      value={{
        token: state.token,
        isAuthenticated: state.isAuthenticated,
        user: state.user,
        loading: state.loading,
        error: state.error,
        register,
      }}
    >
      {props.children}
    </AuthContext.Provider>
  );
};
export default AuthState;
